import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 51.1-124.33 Deposit of trust funds not an investment; authorized deposits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-124.33.md","filePath":"vacode/51.1-124.33.md"}'),o={name:"vacode/51.1-124.33.md"},s=n('<h1 id="§-51-1-124-33-deposit-of-trust-funds-not-an-investment-authorized-deposits" tabindex="-1">§ 51.1-124.33 Deposit of trust funds not an investment; authorized deposits. <a class="header-anchor" href="#§-51-1-124-33-deposit-of-trust-funds-not-an-investment-authorized-deposits" aria-label="Permalink to &quot;§ 51.1-124.33 Deposit of trust funds not an investment; authorized deposits.&quot;">​</a></h1><p>The Board may authorize the deposit of trust funds in interest-bearing time deposits and certificates of deposit of national banks located within the Commonwealth, of banks organized pursuant to Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-800/" target="_blank" rel="noreferrer">6.2-800</a> et seq.) of Title 6.2, of savings institutions which are under state supervision, and of federal associations located in this Commonwealth and organized under the laws of the United States and under federal supervision and federally insured. Such deposits shall not be considered the investment of trust funds for the purposes of this chapter. Deposit of the funds in demand and time deposits and in certificates of deposit of national banks located within this Commonwealth and of banks organized pursuant to the Virginia Banking Act is hereby authorized, provided that the deposits are secured as provided by law and that no deposit is made for any one period in excess of one year. Deposit of trust funds in savings accounts and certificates of savings institutions which are under state supervision and of federal associations located in this Commonwealth and organized under the laws of the United States and under federal supervision is hereby authorized. Such deposits shall not exceed the amount insured by the Federal Deposit Insurance Corporation or other federal insurance agency, unless deposits in excess of the amount insured are fully collateralized by eligible collateral as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-4401/" target="_blank" rel="noreferrer">2.2-4401</a>. No such deposit shall be made for any one period in excess of one year.</p><p>1980, c. 596, § 51-111.24:4; 1990, cc. 3, 832, § 51.1-122; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>.</p>',3),i=[s];function r(d,l,h,f,c,u){return t(),a("div",null,i)}const m=e(o,[["render",r]]);export{_ as __pageData,m as default};
