import{_ as e,o as r,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 51.1-124.9 Changes or errors in records resulting in erroneous payments; employer liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-124.9.md","filePath":"vacode/51.1-124.9.md"}'),o={name:"vacode/51.1-124.9.md"},t=a('<h1 id="§-51-1-124-9-changes-or-errors-in-records-resulting-in-erroneous-payments-employer-liability" tabindex="-1">§ 51.1-124.9 Changes or errors in records resulting in erroneous payments; employer liability. <a class="header-anchor" href="#§-51-1-124-9-changes-or-errors-in-records-resulting-in-erroneous-payments-employer-liability" aria-label="Permalink to &quot;§ 51.1-124.9 Changes or errors in records resulting in erroneous payments; employer liability.&quot;">​</a></h1><p>A. 1. If any change or error in records results in any member or beneficiary receiving more or less than he would have been entitled to receive from the Retirement System had the records been correct, the Board shall, subject to the provisions of subsection B, correct the error and as far as practicable adjust the payments so that the actuarial equivalent of the correct benefit shall be paid.</p><ol start="2"><li><p>If a member has been overpaid through no fault of his and could not reasonably have been expected to detect the error the Board may waive any repayment which it believes would cause hardship.</p></li><li><p>Upon determination that any person has erroneously been included in membership in the Retirement System, accumulated contributions resulting from the erroneous membership shall be refunded.</p></li></ol><p>B. The Board is authorized to recover any overpayments, from an employer found to be responsible for such overpayments, to a member or beneficiary (i) whose average final compensation exceeds the limitation in § <a href="http://law.lis.virginia.gov/vacode/51.1-152/" target="_blank" rel="noreferrer">51.1-152</a>, (ii) who receives in-service distributions because the member or beneficiary is rehired by the employer without either a bona fide break in service, as determined by the Board, following retirement, or the break in service required under subdivision B 3 (a) of § <a href="http://law.lis.virginia.gov/vacode/51.1-155/" target="_blank" rel="noreferrer">51.1-155</a>, or (iii) who the Board determines was in service as an employee covered for retirement purposes as prohibited by subdivision B 1 of § <a href="http://law.lis.virginia.gov/vacode/51.1-155/" target="_blank" rel="noreferrer">51.1-155</a>.</p><p>1952, c. 157, § 51-111.67; 1956, c. 690; 1976, c. 541; 1990, c. 832, § 51.1-107; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0004" target="_blank" rel="noreferrer">4</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0085" target="_blank" rel="noreferrer">85</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',5),n=[t];function s(l,c,h,d,p,m){return r(),i("div",null,n)}const f=e(o,[["render",s]]);export{u as __pageData,f as default};
