import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.1-1302 Local benefit restoration plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1302.md","filePath":"vacode/51.1-1302.md"}'),r={name:"vacode/51.1-1302.md"},n=o('<h1 id="§-51-1-1302-local-benefit-restoration-plans" tabindex="-1">§ 51.1-1302 Local benefit restoration plans. <a class="header-anchor" href="#§-51-1-1302-local-benefit-restoration-plans" aria-label="Permalink to &quot;§ 51.1-1302 Local benefit restoration plans.&quot;">​</a></h1><p>The governing body of any county, municipality, authority or other political subdivision of the Commonwealth that does not participate in the retirement system or that maintains a plan that provides supplemental benefits to employees covered by the retirement system may, by ordinance or by resolution adopted by a recorded majority of the members of such governing body, establish for itself and its employees a benefit restoration plan established under § 415 (m) of the Internal Revenue Code and, if it deems it advisable, may create a trust or other special fund for segregation of funds or assets relating to such plan.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0692" target="_blank" rel="noreferrer">692</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>.</p>',3),i=[n];function s(l,c,p,d,f,h){return t(),a("div",null,i)}const b=e(r,[["render",s]]);export{m as __pageData,b as default};
