import{_ as e,o as t,c as n,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 51.1-1304 Other retirement, pension systems not affected; annual report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1304.md","filePath":"vacode/51.1-1304.md"}'),a={name:"vacode/51.1-1304.md"},o=r('<h1 id="§-51-1-1304-other-retirement-pension-systems-not-affected-annual-report" tabindex="-1">§ 51.1-1304 Other retirement, pension systems not affected; annual report. <a class="header-anchor" href="#§-51-1-1304-other-retirement-pension-systems-not-affected-annual-report" aria-label="Permalink to &quot;§ 51.1-1304 Other retirement, pension systems not affected; annual report.&quot;">​</a></h1><p>A. Any benefit restoration plan established by this chapter, and any plan adopted hereunder, shall exist and serve in addition to any other retirement, pension or benefit system established by the Commonwealth, its agencies, counties, municipalities or other political subdivisions, and shall not supersede, make inoperative or reduce any benefits provided by the Retirement System or by any other retirement, pension or benefit program established by law.</p><p>B. The Retirement System shall submit an annual report to the Governor and the General Assembly annually on or before December 31 advising them of the condition and all operational costs associated with the benefit restoration plan.</p><p>2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0692" target="_blank" rel="noreferrer">692</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>.</p>',4),i=[o];function s(l,p,c,d,h,m){return t(),n("div",null,i)}const b=e(a,[["render",s]]);export{_ as __pageData,b as default};
