import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.1-1402 Health insurance credits for retired local government employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-1402.md","filePath":"vacode/51.1-1402.md"}'),a={name:"vacode/51.1-1402.md"},o=i('<h1 id="§-51-1-1402-health-insurance-credits-for-retired-local-government-employees" tabindex="-1">§ 51.1-1402 Health insurance credits for retired local government employees. <a class="header-anchor" href="#§-51-1-1402-health-insurance-credits-for-retired-local-government-employees" aria-label="Permalink to &quot;§ 51.1-1402 Health insurance credits for retired local government employees.&quot;">​</a></h1><p>A. Retired local government employees, other than employees of a local school division who are not teachers as defined in § <a href="/vacode/51.1-124.3/">51.1-124.3</a>, whose localities have elected to participate in the Virginia Retirement System, including the hybrid retirement program described in § <a href="/vacode/51.1-169/">51.1-169</a>, who have rendered at least 15 years of total creditable service under the System shall receive a health insurance credit to their monthly retirement allowance, which shall be applied to reduce the retired member&#39;s health insurance premium cost, provided the retiree&#39;s employer elects to participate in the credit program. The amount of each monthly health insurance credit payable under this section shall be $1.50 for each full year of the retired member&#39;s creditable service, not to exceed a maximum monthly credit of $45; however, each former member whose retirement was for disability, a participant receiving long-term disability pursuant to § <a href="/vacode/51.1-1157/">51.1-1157</a> or <a href="/vacode/51.1-1165/">51.1-1165</a>, or a member of the hybrid retirement program receiving long-term disability pursuant to coverage under subsection A of § <a href="/vacode/51.1-1153/">51.1-1153</a> shall receive a monthly health insurance credit of $45. Eligibility for the credit shall be determined in a manner prescribed by the Virginia Retirement System. Any member who elects to defer his retirement pursuant to subsection C of § <a href="/vacode/51.1-153/">51.1-153</a> shall be entitled to receive the allowable credit provided by this section on the effective date of his retirement.</p><p>B. Those retired employees who purchase an alternative policy from a carrier or organization of their own choosing shall be eligible to receive a credit in the amount specified in subsection C. Eligibility for the credit and payment of the credit shall be determined in a manner prescribed by the Virginia Retirement System.</p><p>C. The credit shall be in the amount provided in subsection A or the amount of premium paid for the personal health insurance policy, whichever is less.</p><p>D. The cost of the monthly health insurance credit payable under this section shall be borne by the locality.</p><p>E. The Virginia Retirement System shall actuarially determine the amount necessary to fund all credits provided under this section, reflect the cost of such credits in the applicable employer contribution rate pursuant to § <a href="/vacode/51.1-145/">51.1-145</a>, and prescribe such terms and conditions as are necessary to carry out the provisions of this section. The costs associated with the administration of the health insurance credit program provided for in this section shall be recovered from the health insurance credit trust fund.</p><p>1992, c. 770, § 2.1-20.1:4; 1993, cc. 743, 938; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0195" target="_blank" rel="noreferrer">195</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1091" target="_blank" rel="noreferrer">1091</a>.</p>',7),n=[o];function l(c,s,h,d,p,m){return r(),t("div",null,n)}const u=e(a,[["render",l]]);export{g as __pageData,u as default};
