import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.1-155.2 Exceptions from general early retirement provisions for certain local government officials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-155.2.md","filePath":"vacode/51.1-155.2.md"}'),i={name:"vacode/51.1-155.2.md"},o=t('<h1 id="§-51-1-155-2-exceptions-from-general-early-retirement-provisions-for-certain-local-government-officials" tabindex="-1">§ 51.1-155.2 Exceptions from general early retirement provisions for certain local government officials. <a class="header-anchor" href="#§-51-1-155-2-exceptions-from-general-early-retirement-provisions-for-certain-local-government-officials" aria-label="Permalink to &quot;§ 51.1-155.2 Exceptions from general early retirement provisions for certain local government officials.&quot;">​</a></h1><p>A. The provisions of this section apply to any member of the retirement system who (i) is appointed county administrator pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-406/" target="_blank" rel="noreferrer">15.2-406</a> or <a href="http://law.lis.virginia.gov/vacode/15.2-1540/" target="_blank" rel="noreferrer">15.2-1540</a>, urban county executive pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-804/" target="_blank" rel="noreferrer">15.2-804</a>, county executive pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-509/" target="_blank" rel="noreferrer">15.2-509</a>, county manager pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-609/" target="_blank" rel="noreferrer">15.2-609</a> or <a href="http://law.lis.virginia.gov/vacode/15.2-702/" target="_blank" rel="noreferrer">15.2-702</a>, county administrator or city or town manager pursuant to Chapter 15 (§ <a href="http://law.lis.virginia.gov/vacode/15.2-1500/" target="_blank" rel="noreferrer">15.2-1500</a> et seq.) of Title 15.2 or county, city or town attorney pursuant to § <a href="http://law.lis.virginia.gov/vacode/15.2-1542/" target="_blank" rel="noreferrer">15.2-1542</a>; (ii) is involuntarily separated from service; and (iii) has 20 or more years of creditable service at the date of separation. Such member may retire without the reduction in retirement allowance required by subdivisions A 2 and A 3 of § <a href="http://law.lis.virginia.gov/vacode/51.1-155/" target="_blank" rel="noreferrer">51.1-155</a> upon attaining age 50, provided, however, that if (a) the member is a person who becomes a member on or after July 1, 2010, (b) the member does not have at least 60 months of creditable service as of January 1, 2013, or (c) the member is enrolled in the hybrid retirement program described in § <a href="http://law.lis.virginia.gov/vacode/51.1-169/" target="_blank" rel="noreferrer">51.1-169</a>, then the member may retire without the reduction in retirement allowance required by subdivisions A 2 and A 3 of § <a href="http://law.lis.virginia.gov/vacode/51.1-155/" target="_blank" rel="noreferrer">51.1-155</a> upon attaining age 60.</p><p>B. For the purposes of this section, &quot;involuntary separation&quot; means any dismissal, requested resignation, or failure to obtain reappointment, except in case of a conviction for a felony or crime involving moral turpitude or dishonesty.</p><p>C. The cost of this provision shall be borne by the locality.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0662" target="_blank" rel="noreferrer">662</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0924" target="_blank" rel="noreferrer">924</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0343" target="_blank" rel="noreferrer">343</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0911" target="_blank" rel="noreferrer">911</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0537" target="_blank" rel="noreferrer">537</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0880" target="_blank" rel="noreferrer">880</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0356" target="_blank" rel="noreferrer">356</a>.</p>',5),n=[o];function l(s,c,g,f,p,v){return r(),a("div",null,n)}const u=e(i,[["render",l]]);export{m as __pageData,u as default};
