import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 51.1-159 Medical examinations of persons retired for disability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-159.md","filePath":"vacode/51.1-159.md"}'),r={name:"vacode/51.1-159.md"},o=t('<h1 id="§-51-1-159-medical-examinations-of-persons-retired-for-disability" tabindex="-1">§ 51.1-159 Medical examinations of persons retired for disability. <a class="header-anchor" href="#§-51-1-159-medical-examinations-of-persons-retired-for-disability" aria-label="Permalink to &quot;§ 51.1-159 Medical examinations of persons retired for disability.&quot;">​</a></h1><p>A. Once each year following retirement, the Board may require a former member who retired for disability and who has not attained his normal retirement age to undergo a medical examination by the Medical Board or a physician or other health care professional designated by the Medical Board. If the former member refuses to submit to the required medical examination, his retirement allowance shall be discontinued until he complies. If he does not comply within six months of the date of the request, all of his rights to any further disability retirement allowance shall cease, subject to the provisions of § <a href="/vacode/51.1-160/">51.1-160</a>.</p><p>B. If the Medical Board determines that a beneficiary is not disabled after reviewing the findings of any of the medical examinations provided for in this section, all rights to any further disability allowance shall cease, subject to the provisions of § <a href="/vacode/51.1-160/">51.1-160</a>.</p><p>1952, c. 157, § 51-111.61; 1984, c. 430; 1990, c. 832; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',4),n=[o];function s(l,d,c,h,f,m){return a(),i("div",null,n)}const b=e(r,[["render",s]]);export{_ as __pageData,b as default};
