import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 51.1-513.2 Long-term care coverage program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-513.2.md","filePath":"vacode/51.1-513.2.md"}'),t={name:"vacode/51.1-513.2.md"},i=o('<h1 id="§-51-1-513-2-long-term-care-coverage-program" tabindex="-1">§ 51.1-513.2 Long-term care coverage program. <a class="header-anchor" href="#§-51-1-513-2-long-term-care-coverage-program" aria-label="Permalink to &quot;§ 51.1-513.2 Long-term care coverage program.&quot;">​</a></h1><p>A. The Board shall maintain and administer a long-term care coverage or similar benefit program for any state employee working an average of at least 20 hours per week, and for any other person who has five or more years of creditable service with any retirement plan administered by the Virginia Retirement System. The long-term care coverage program may also extend coverage to eligible family members of such state employee or other person. The Board is authorized to contract for and purchase insurance coverage or to use other actuarially sound funding necessary to effectuate this provision. Participation in the long-term care coverage program shall be voluntary, subject to policies and procedures adopted by the Board.</p><p>B. Any person eligible to participate in the long-term care coverage program pursuant to § <a href="/vacode/51.1-513.3/">51.1-513.3</a> will not be eligible for this plan.</p><p>C. Notwithstanding the provisions of subsection A, the Board may self-insure long-term care benefits provided under § <a href="/vacode/51.1-513.2/">51.1-513.2</a> or <a href="/vacode/51.1-513.3/">51.1-513.3</a> in accordance with the standards set forth in § <a href="/vacode/51.1-124.30/">51.1-124.30</a>.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0568" target="_blank" rel="noreferrer">568</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>.</p>',5),n=[i];function c(s,l,g,p,h,d){return r(),a("div",null,n)}const v=e(t,[["render",c]]);export{f as __pageData,v as default};
