import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 51.1-618 Defined contribution plan for eligible employees of local public school boards; administered by the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.1-618.md","filePath":"vacode/51.1-618.md"}'),a={name:"vacode/51.1-618.md"},r=o('<h1 id="§-51-1-618-defined-contribution-plan-for-eligible-employees-of-local-public-school-boards-administered-by-the-board" tabindex="-1">§ 51.1-618 Defined contribution plan for eligible employees of local public school boards; administered by the Board. <a class="header-anchor" href="#§-51-1-618-defined-contribution-plan-for-eligible-employees-of-local-public-school-boards-administered-by-the-board" aria-label="Permalink to &quot;§ 51.1-618 Defined contribution plan for eligible employees of local public school boards; administered by the Board.&quot;">​</a></h1><p>A. The Board shall establish and administer a defined contribution plan for eligible employees. The Director of the Virginia Retirement System shall be the chief administrative officer of the plan. The Board may contract with private corporations or institutions subject to the standards set forth in § <a href="http://law.lis.virginia.gov/vacode/51.1-124.30/" target="_blank" rel="noreferrer">51.1-124.30</a> to provide investment products as well as any other goods and services related to the administration of the plan. The Virginia Retirement System is hereby authorized to perform related services including, but not limited to, providing consolidated billing, individual and collective recordkeeping and accountings, and asset purchase, control, and safekeeping.</p><p>B. If it deems it advisable, the Board may create a trust or other special fund for the segregation of the funds or assets resulting from contributions made on behalf of qualified participants.</p><p>C. Participating employers shall be responsible for setting the contribution rate for their eligible employees and timely transferring contributions to the private corporation or institution designated by the Board pursuant to subsection A.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0245" target="_blank" rel="noreferrer">245</a>.</p>',5),n=[r];function l(s,d,c,p,h,f){return t(),i("div",null,n)}const m=e(a,[["render",l]]);export{u as __pageData,m as default};
