import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.5-128 Duties of the Commonwealth Council on Aging.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-128.md","filePath":"vacode/51.5-128.md"}'),a={name:"vacode/51.5-128.md"},r=n('<h1 id="§-51-5-128-duties-of-the-commonwealth-council-on-aging" tabindex="-1">§ 51.5-128 Duties of the Commonwealth Council on Aging. <a class="header-anchor" href="#§-51-5-128-duties-of-the-commonwealth-council-on-aging" aria-label="Permalink to &quot;§ 51.5-128 Duties of the Commonwealth Council on Aging.&quot;">​</a></h1><p>A. The Commonwealth Council on Aging shall have the following duties:</p><ol><li><p>Examine the needs of older Virginians and their caregivers and ways in which state government can most effectively and efficiently assist in meeting those needs;</p></li><li><p>Advise the Governor and General Assembly on aging issues and aging policy for the Commonwealth;</p></li><li><p>Advise the Governor on any proposed regulations deemed by the Director of the Department of Planning and Budget to have a substantial and distinct impact on older Virginians and their caregivers. Such advice shall be provided in addition to other regulatory reviews required by the Administrative Process Act (§ <a href="/vacode/2.2-4000/">2.2-4000</a> et seq.);</p></li><li><p>Advocate for and assist in developing the Commonwealth&#39;s planning for meeting the needs of the growing number of older Virginians and their caregivers; and</p></li><li><p>Assist and advise the Department regarding strategies to improve nutritional health, alleviate hunger, and prevent malnutrition among older adults.</p></li></ol><p>B. The Commonwealth Council on Aging may apply for and expend such grants, gifts, or bequests from any source as may become available in connection with its duties under this section, and may comply with such conditions and requirements as may be imposed in connection therewith.</p><p>C. The Commonwealth Council on Aging shall submit to the Governor, General Assembly, and Department by October 1 of each year an electronic report regarding the activities and recommendations of the Commonwealth Council on Aging, which shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website and the Department&#39;s website.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0665" target="_blank" rel="noreferrer">665</a>, § <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>.1-373.02; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0877" target="_blank" rel="noreferrer">877</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0896" target="_blank" rel="noreferrer">896</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>.2-2627; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0411" target="_blank" rel="noreferrer">411</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0202" target="_blank" rel="noreferrer">202</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0728" target="_blank" rel="noreferrer">728</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0533" target="_blank" rel="noreferrer">533</a>.</p>',6),o=[r];function l(s,g,h,c,d,p){return i(),t("div",null,o)}const u=e(a,[["render",l]]);export{m as __pageData,u as default};
