import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 51.5-137 Administrative responsibilities of Department regarding aging services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-137.md","filePath":"vacode/51.5-137.md"}'),t={name:"vacode/51.5-137.md"},n=a('<h1 id="§-51-5-137-administrative-responsibilities-of-department-regarding-aging-services" tabindex="-1">§ 51.5-137 Administrative responsibilities of Department regarding aging services. <a class="header-anchor" href="#§-51-5-137-administrative-responsibilities-of-department-regarding-aging-services" aria-label="Permalink to &quot;§ 51.5-137 Administrative responsibilities of Department regarding aging services.&quot;">​</a></h1><p>The Department shall have the following responsibilities regarding aging services in the Commonwealth:</p><ol><li><p>Develop appropriate fiscal and administrative controls over aging services in the Commonwealth;</p></li><li><p>Develop a state long-term care plan to guide the coordination and delivery of aging services. The plan shall ensure the development of a continuum of aging services for older persons in need of services;</p></li><li><p>Identify and assure the equitable statewide distribution of resources for aging services; and</p></li><li><p>Perform ongoing evaluations of the cost-effective utilization of aging services.</p></li></ol><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0728" target="_blank" rel="noreferrer">728</a>.</p>',4),s=[n];function o(l,g,c,p,d,f){return i(),r("div",null,s)}const h=e(t,[["render",o]]);export{_ as __pageData,h as default};
