import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse(`{"title":"§ 51.5-152 Powers and duties of the Department with respect to Alzheimer's disease and related disorders.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-152.md","filePath":"vacode/51.5-152.md"}`),s={name:"vacode/51.5-152.md"},t=a('<h1 id="§-51-5-152-powers-and-duties-of-the-department-with-respect-to-alzheimer-s-disease-and-related-disorders" tabindex="-1">§ 51.5-152 Powers and duties of the Department with respect to Alzheimer&#39;s disease and related disorders. <a class="header-anchor" href="#§-51-5-152-powers-and-duties-of-the-department-with-respect-to-alzheimer-s-disease-and-related-disorders" aria-label="Permalink to &quot;§ 51.5-152 Powers and duties of the Department with respect to Alzheimer&#39;s disease and related disorders.&quot;">​</a></h1><p>The Department shall:</p><ol><li><p>Serve as a referral point for linking families caring for persons with Alzheimer&#39;s disease and related disorders with Virginia&#39;s chapters of the Alzheimer&#39;s Disease and Related Disorders Association;</p></li><li><p>Provide information, counseling, education, and referral about services and programs, including safe, secure environments as defined in § <a href="/vacode/63.2-1802/">63.2-1802</a>, that may support individuals and families dealing with Alzheimer&#39;s disease and related disorders;</p></li><li><p>Collect and monitor data related to the impact of Alzheimer&#39;s disease and related disorders on Virginians;</p></li><li><p>Evaluate the needs of individuals with Alzheimer&#39;s disease and related disorders and their caregivers, and identify the services, resources, and policies that may be needed to address such needs for individuals with Alzheimer&#39;s disease and related disorders and their caregivers;</p></li><li><p>Recommend strategies for coordination of services and resources among agencies involved in the delivery of services to Virginians with Alzheimer&#39;s disease and related disorders;</p></li><li><p>Monitor development and implementation of the state plan for meeting the needs of individuals with Alzheimer&#39;s disease and related disorders and their caregivers required pursuant to subdivision D 4 of § <a href="/vacode/51.5-154/">51.5-154</a>; and</p></li><li><p>Recommend policies, legislation, and funding necessary to implement the state plan for meeting the needs of individuals with Alzheimer&#39;s disease and related disorders and their caregivers required pursuant to subdivision D 4 of § <a href="/vacode/51.5-154/">51.5-154</a>.</p></li></ol><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0766" target="_blank" rel="noreferrer">766</a>, § 2.2-718; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0376" target="_blank" rel="noreferrer">376</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0728" target="_blank" rel="noreferrer">728</a>.</p>',4),d=[t];function n(o,l,h,c,p,f){return i(),r("div",null,d)}const v=e(s,[["render",n]]);export{m as __pageData,v as default};
