import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 51.5-153 Alzheimer's and Related Diseases Research Award Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-153.md","filePath":"vacode/51.5-153.md"}`),s={name:"vacode/51.5-153.md"},i=t('<h1 id="§-51-5-153-alzheimer-s-and-related-diseases-research-award-fund" tabindex="-1">§ 51.5-153 Alzheimer&#39;s and Related Diseases Research Award Fund. <a class="header-anchor" href="#§-51-5-153-alzheimer-s-and-related-diseases-research-award-fund" aria-label="Permalink to &quot;§ 51.5-153 Alzheimer&#39;s and Related Diseases Research Award Fund.&quot;">​</a></h1><p>There is established a fund to be known as the Alzheimer&#39;s and Related Diseases Research Award Fund, hereafter referred to as &quot;the Fund.&quot; The Fund shall be administered by the Virginia Center on Aging and the awards shall be made through an awards committee consisting of representatives from the scientific and medical communities and the general public. The awards shall be given annually to scientists in Virginia in order to support research into the causes of Alzheimer&#39;s disease and related disorders, methods of treatment, ways that families can cope with the stresses of the disease, and the impact of the disease on the citizens of the Commonwealth.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0766" target="_blank" rel="noreferrer">766</a>, § 2.2-719; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),n=[i];function d(o,l,h,c,f,_){return a(),r("div",null,n)}const p=e(s,[["render",d]]);export{g as __pageData,p as default};
