import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.5-161 Awarding of grants; purposes; eligible applicants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-161.md","filePath":"vacode/51.5-161.md"}'),t={name:"vacode/51.5-161.md"},o=n('<h1 id="§-51-5-161-awarding-of-grants-purposes-eligible-applicants" tabindex="-1">§ 51.5-161 Awarding of grants; purposes; eligible applicants. <a class="header-anchor" href="#§-51-5-161-awarding-of-grants-purposes-eligible-applicants" aria-label="Permalink to &quot;§ 51.5-161 Awarding of grants; purposes; eligible applicants.&quot;">​</a></h1><p>A. The Department is authorized, subject to other requirements of this law, to make grants or enter into contracts, in accordance with rules and regulations of the Commissioner, for the following purposes:</p><ol><li>To promote a philosophy of independent living, including a philosophy of consumer control, peer support, self-help, self-determination, equal access, and individual and system advocacy, in order to maximize the leadership, empowerment, independence, and productivity of individuals with disabilities and the integration and full inclusion of individuals with disabilities into the mainstream of society by:</li></ol><p>a. Providing financial assistance for expanding and improving the provision of independent living services; and</p><p>b. Providing financial assistance to develop and support a statewide network of centers for independent living; and</p><ol start="2"><li>To assist employers in employing, training, and providing other related services to persons with significant disabilities.</li></ol><p>B. Applications for the grants and contracts authorized in subdivision A 1 may be made by consumer-controlled, community-based, cross-disability, nonresidential, private nonprofit agencies that (i) are designed and operated within a local community by individuals with disabilities and (ii) provide an array of independent living services. Each applicant shall be established and shall have a governing board, the majority of whose members shall be persons with disabilities, for the sole purpose of operating the independent living center.</p><p>C. Applications for the grants and contracts authorized in subdivision A 2 may be made by employers in the Commonwealth who wish to take affirmative steps to employ and advance in employment persons with disabilities.</p><p>1985, c. 421, §§ 51.01-23, 51.5-23; 1992, c. 755; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0007" target="_blank" rel="noreferrer">7</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',9),r=[o];function s(l,p,d,c,g,h){return i(),a("div",null,r)}const u=e(t,[["render",s]]);export{m as __pageData,u as default};
