import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 51.5-166 Eligibility criteria.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-166.md","filePath":"vacode/51.5-166.md"}'),a={name:"vacode/51.5-166.md"},o=r('<h1 id="§-51-5-166-eligibility-criteria" tabindex="-1">§ 51.5-166 Eligibility criteria. <a class="header-anchor" href="#§-51-5-166-eligibility-criteria" aria-label="Permalink to &quot;§ 51.5-166 Eligibility criteria.&quot;">​</a></h1><p>The Commissioner shall establish eligibility criteria for services to be applied by programs awarded grants pursuant to this article. Such criteria shall provide that:</p><ol><li><p>Eligibility shall be determined without regard to sex, sexual orientation, gender identity, race, national origin, religion, or type of impairment of the person applying for the service;</p></li><li><p>Preference shall be given to applicants for services whose impairments are so severe that they do not presently have the potential for employment, but whose ability to live and function independently within their family settings or communities may be improved by the services for which they have applied; and</p></li><li><p>Services shall not be provided to people who are eligible for prevocational or supported employment services through a Medicaid home and community based waiver program.</p></li></ol><p>1985, c. 421, §§ 51.01-27, 51.5-27; 1992, c. 755; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>.</p>',4),l=[o];function n(s,c,p,h,d,g){return i(),t("div",null,l)}const f=e(a,[["render",n]]);export{m as __pageData,f as default};
