import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 51.5-171 Assessment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-171.md","filePath":"vacode/51.5-171.md"}'),i={name:"vacode/51.5-171.md"},r=s('<h1 id="§-51-5-171-assessment" tabindex="-1">§ 51.5-171 Assessment. <a class="header-anchor" href="#§-51-5-171-assessment" aria-label="Permalink to &quot;§ 51.5-171 Assessment.&quot;">​</a></h1><p>When an individual applies for vocational rehabilitation services provided or funded by the Department, in whole or in part, an assessment shall be made to determine eligibility for benefits according to the standards adopted pursuant to § <a href="http://law.lis.virginia.gov/vacode/51.5-170/" target="_blank" rel="noreferrer">51.5-170</a>. If, after an assessment, eligibility is established, a comprehensive assessment shall be conducted to ascertain the nature and scope of services needed by the applicant. Both assessments shall be carried out with the involvement of the applicant and his parents or guardian if appropriate. Both assessments shall include, when appropriate, (i) a review of existing data and, to the extent necessary, the provision of appropriate assessment activities to obtain necessary data to determine eligibility and services needed; (ii) referral for the provision of rehabilitation technology services to assess and develop the individual&#39;s capacities to perform in a work environment; and (iii) referral to other agencies and organizations for appropriate assessment services.</p><p>1985, c. 421, §§ 51.01-16, 51.5-16; 1989, c. 181; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0046" target="_blank" rel="noreferrer">46</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',3),n=[r];function o(l,c,d,p,h,f){return a(),t("div",null,n)}const m=e(i,[["render",o]]);export{v as __pageData,m as default};
