import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 51.5-179 Commonwealth Neurotrauma Initiative Trust Fund established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-179.md","filePath":"vacode/51.5-179.md"}'),i={name:"vacode/51.5-179.md"},n=r('<h1 id="§-51-5-179-commonwealth-neurotrauma-initiative-trust-fund-established" tabindex="-1">§ 51.5-179 Commonwealth Neurotrauma Initiative Trust Fund established. <a class="header-anchor" href="#§-51-5-179-commonwealth-neurotrauma-initiative-trust-fund-established" aria-label="Permalink to &quot;§ 51.5-179 Commonwealth Neurotrauma Initiative Trust Fund established.&quot;">​</a></h1><p>A. For the purpose of preventing traumatic spinal cord or brain injuries and improving the treatment and care of Virginians with traumatic spinal cord or brain injuries, there is hereby created in the state treasury a special nonreverting fund to be known as the Commonwealth Neurotrauma Initiative Trust Fund, hereinafter referred to as the &quot;Fund.&quot; The Fund shall be established on the books of the Comptroller as a revolving fund and shall be administered by the Commonwealth Neurotrauma Initiative Advisory Board in cooperation with the Commissioner. The Fund shall consist of grants, donations, and bequests from public and private sources and funds collected as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-411/" target="_blank" rel="noreferrer">46.2-411</a>. Such moneys shall be deposited into the state treasury to the credit of the Fund and shall be used for the purposes of this article.</p><p>B. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. The Fund shall be administered by the Commissioner and distributed according to the grant procedures established pursuant to § <a href="http://law.lis.virginia.gov/vacode/51.5-181/" target="_blank" rel="noreferrer">51.5-181</a>. Moneys in the Fund shall be used to support grants for Virginia-based organizations, institutions, and researchers as follows: (i) 47.5 percent shall be allocated for research on the mechanisms and treatment of neurotrauma, (ii) 47.5 percent shall be allocated for rehabilitative services, and (iii) five percent shall be allocated for the Department&#39;s costs for administering and staffing the Commonwealth Neurotrauma Initiative Advisory Board.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0060" target="_blank" rel="noreferrer">60</a>, § 51.5-12.2; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',4),o=[n];function s(l,d,h,u,c,m){return t(),a("div",null,o)}const b=e(i,[["render",s]]);export{p as __pageData,b as default};
