import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 51.5-33 Powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-33.md","filePath":"vacode/51.5-33.md"}'),r={name:"vacode/51.5-33.md"},n=t('<h1 id="§-51-5-33-powers-and-duties" tabindex="-1">§ 51.5-33 Powers and duties. <a class="header-anchor" href="#§-51-5-33-powers-and-duties" aria-label="Permalink to &quot;§ 51.5-33 Powers and duties.&quot;">​</a></h1><p>The Board shall have the following powers and duties:</p><ol><li><p>To advise the Secretary of Health and Human Resources and Governor on issues and problems of interest to persons with disabilities and on such other matters as either the Secretary or the Governor may request;</p></li><li><p>Beginning July 1, 2017, to submit an annual report to the Governor, through the Secretary of Health and Human Resources, that provides an in-depth assessment of at least two major service areas for people with disabilities, to be determined by the Board, that (i) includes a description of critical issues and trend analyses, (ii) identifies the needs of persons with developmental and related disabilities, (iii) evaluates the effectiveness of services provided by state-supported programs, and (iv) makes programmatic and fiscal recommendations for improving services and supports. Once every four years, the Board shall make available to the public all the service areas it intends to review over the following four years and shall ensure that each of these service areas is reviewed at least once within such period.</p></li><li><p>To serve as the State Council on Developmental Disabilities for the administration of certain federal public health and welfare laws as provided in 42 U.S.C. § 15001;</p></li><li><p>To perform all duties and exercise all powers designated by federal law for such state councils on developmental disabilities, including the responsibility for planning activities on behalf of all developmentally disabled persons in the Commonwealth; for receiving, accounting for and disbursing federal funds; for developing and approving the state plan; and for monitoring and evaluating the implementation of such plan for the provision of services and facilities for persons with developmental disabilities;</p></li><li><p>To be responsible for obtaining information and data from within the Commonwealth, and from time to time, but not less than annually, to review and evaluate the state plan and submit such state plan, and revisions thereto, to the Governor and to the U.S. Secretary of Health and Human Services;</p></li><li><p>To hire and supervise the Director of the Board and prescribe his duties, including:</p></li></ol><p>a. Hiring such staff and obtaining the service of such professional, technical, and clerical personnel necessary to carry out the Board&#39;s powers and duties; and</p><p>b. Accepting gifts and grants on behalf of the Commonwealth, in furtherance of the purpose of this Board.</p><p>1985, c. 421, § 51.01-33; 1990, c. 458; 1992, c. 627; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0034" target="_blank" rel="noreferrer">34</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0516" target="_blank" rel="noreferrer">516</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0523" target="_blank" rel="noreferrer">523</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0219" target="_blank" rel="noreferrer">219</a>.</p>',6),o=[n];function s(l,d,h,c,p,f){return a(),i("div",null,o)}const g=e(r,[["render",s]]);export{v as __pageData,g as default};
