import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 51.5-58 Provision of loans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-58.md","filePath":"vacode/51.5-58.md"}'),r={name:"vacode/51.5-58.md"},i=a('<h1 id="§-51-5-58-provision-of-loans" tabindex="-1">§ 51.5-58 Provision of loans. <a class="header-anchor" href="#§-51-5-58-provision-of-loans" aria-label="Permalink to &quot;§ 51.5-58 Provision of loans.&quot;">​</a></h1><p>A. The Board may enter into loan agreements with any qualifying borrower who demonstrates (i) that the loan will be used to acquire assistive technology, other equipment, or other authorized purposes designed to help one or more persons with disabilities to improve their independence or become more productive members of the community and (ii) who has the ability to repay the loan.</p><p>B. The amount and terms of any loan shall be determined by the Board.</p><p>C. All loans must be repaid on such terms and at such interest rates as the Board may, from time to time, determine to be appropriate in accordance with a procedure prescribed by regulations adopted pursuant to the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.).</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0812" target="_blank" rel="noreferrer">812</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0019" target="_blank" rel="noreferrer">19</a>.</p>',5),n=[i];function s(l,d,c,p,h,m){return o(),t("div",null,n)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
