import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 51.5-62 Powers and duties of Board; form of materials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-62.md","filePath":"vacode/51.5-62.md"}'),t={name:"vacode/51.5-62.md"},i=a('<h1 id="§-51-5-62-powers-and-duties-of-board-form-of-materials" tabindex="-1">§ 51.5-62 Powers and duties of Board; form of materials. <a class="header-anchor" href="#§-51-5-62-powers-and-duties-of-board-form-of-materials" aria-label="Permalink to &quot;§ 51.5-62 Powers and duties of Board; form of materials.&quot;">​</a></h1><p>A. The Board shall exercise the following general powers and duties:</p><ol><li><p>Advise the Governor, the Secretary of Health and Human Resources, the Commissioner, and the General Assembly on the delivery of public services to and the protection of the rights of persons with disabilities on matters relating to this title, and on such other matters as the Governor, Secretary, Commissioner, or the General Assembly may request; and</p></li><li><p>Review and comment on policies, budgets and requests for appropriations for the Department prior to their submission to the Secretary of Health and Human Resources and the Governor and on applications for federal funds.</p></li></ol><p>B. Material submitted by the Commissioner for review and comment by the Board, when practicable, shall be in the medium or format suitable for review by each member of the Board.</p><p>1992, c. 755, § 63.1-68.1; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',5),s=[i];function n(d,l,c,f,h,m){return r(),o("div",null,s)}const u=e(t,[["render",n]]);export{_ as __pageData,u as default};
