import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 51.5-72 Establishment of rehabilitative manufacturing and service industries; expenditures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/51.5-72.md","filePath":"vacode/51.5-72.md"}'),i={name:"vacode/51.5-72.md"},n=r('<h1 id="§-51-5-72-establishment-of-rehabilitative-manufacturing-and-service-industries-expenditures" tabindex="-1">§ 51.5-72 Establishment of rehabilitative manufacturing and service industries; expenditures. <a class="header-anchor" href="#§-51-5-72-establishment-of-rehabilitative-manufacturing-and-service-industries-expenditures" aria-label="Permalink to &quot;§ 51.5-72 Establishment of rehabilitative manufacturing and service industries; expenditures.&quot;">​</a></h1><p>The Department may (i) establish, equip and maintain rehabilitative manufacturing and service industries for the employment of suitable blind persons, (ii) pay its employees suitable wages and contribute five percent of the creditable compensation of those employees who elect to participate in a before-tax payroll deduction to a tax deferred retirement savings plan established under the United States Internal Revenue Code for nonprofit agencies, and (iii) devise means for the sale and distribution of the products thereof. However, any expenditures made under §§ <a href="/vacode/51.5-63/">51.5-63</a>, <a href="/vacode/51.5-66/">51.5-66</a>, and <a href="/vacode/51.5-72/">51.5-72</a> through <a href="/vacode/51.5-76/">51.5-76</a> shall not exceed the annual appropriation or the amount received by way of bequest or donation during any one year, and no part of the funds appropriated by the Commonwealth for the purposes of §§ <a href="/vacode/51.5-63/">51.5-63</a>, <a href="/vacode/51.5-66/">51.5-66</a>, and <a href="/vacode/51.5-72/">51.5-72</a> through <a href="/vacode/51.5-76/">51.5-76</a> shall be used for solely charitable purposes.</p><p>Code 1950, § 63-167; 1968, c. 578, § 63.1-73; 1980, c. 4; 1990, c. 740; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0711" target="_blank" rel="noreferrer">711</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0731" target="_blank" rel="noreferrer">731</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0013" target="_blank" rel="noreferrer">13</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0184" target="_blank" rel="noreferrer">184</a>.</p>',3),o=[n];function s(l,d,c,h,f,p){return a(),t("div",null,o)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
