import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 52-14 Availability of system.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-14.md","filePath":"vacode/52-14.md"}'),o={name:"vacode/52-14.md"},n=i('<h1 id="§-52-14-availability-of-system" tabindex="-1">§ 52-14 Availability of system. <a class="header-anchor" href="#§-52-14-availability-of-system" aria-label="Permalink to &quot;§ 52-14 Availability of system.&quot;">​</a></h1><p>The communication system provided for in this chapter may be made available for use by any department or division of state government and by any county, city, town, railroad, or other special police department lawfully maintained by any corporation in the Commonwealth as well as agencies of the federal government, subject to the following terms and conditions:</p><ol><li><p>Application for permission to connect with the communication system shall be made to the Superintendent of State Police on forms to be provided by him;</p></li><li><p>Such application may be approved by the Superintendent if, as, and when in his discretion such connection is requisite and necessary for the best interests of the entire system;</p></li><li><p>Upon approval of such application and before the applicant shall be connected with the communication system, such applicant must agree to assume and pay all rentals for sending and receiving stations, or receiving stations only, as may be authorized by the Superintendent for installation within the jurisdiction of the applicant, and any and all costs of installation and operation of such stations; and</p></li><li><p>a. The Commonwealth shall pay all rental for necessary wire or circuit mileage required to connect such stations operated by criminal justice agencies of the Commonwealth and its political subdivisions, or the Federal Bureau of Investigation, with the communication system; and</p></li></ol><p>b. All other agencies shall agree, as a condition of connection or continued service, to assume and pay all rental for necessary wire or circuit mileage required to connect such stations with the communication system.</p><p>1938, p. 674; Michie Code 1942, § 2154(235); 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0150" target="_blank" rel="noreferrer">150</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0049" target="_blank" rel="noreferrer">49</a>.</p>',5),s=[n];function r(l,c,p,d,h,m){return t(),a("div",null,s)}const y=e(o,[["render",r]]);export{u as __pageData,y as default};
