import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 52-25.1 Reporting and return of firearms confiscated or recovered by law-enforcement agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-25.1.md","filePath":"vacode/52-25.1.md"}'),n={name:"vacode/52-25.1.md"},i=t('<h1 id="§-52-25-1-reporting-and-return-of-firearms-confiscated-or-recovered-by-law-enforcement-agencies" tabindex="-1">§ 52-25.1 Reporting and return of firearms confiscated or recovered by law-enforcement agencies. <a class="header-anchor" href="#§-52-25-1-reporting-and-return-of-firearms-confiscated-or-recovered-by-law-enforcement-agencies" aria-label="Permalink to &quot;§ 52-25.1 Reporting and return of firearms confiscated or recovered by law-enforcement agencies.&quot;">​</a></h1><p>A. Whenever a law-enforcement agency confiscates a firearm in connection with a criminal investigation or otherwise recovers a firearm, such agency shall immediately take all appropriate steps to identify and trace the history of such firearm.</p><p>B. The Superintendent shall establish a procedure within the Department of State Police to obtain information regarding all firearms seized, forfeited, found, or otherwise coming into the possession of any state or local law-enforcement agency of the Commonwealth. All law-enforcement agencies of the Commonwealth and of political subdivisions of the Commonwealth shall share with other Virginia law-enforcement agencies all information regarding firearms seized, forfeited, found, or otherwise coming into the agency&#39;s possession that are believed to have been used in the commission of a crime and shall enter such information into a firearms tracing system maintained by the U.S. Department of Justice. The Superintendent shall adopt regulations prescribing the method for reporting this information and the time and manner of submission of the information to a firearms tracing system maintained by the U.S. Department of Justice.</p><p>C. Except as provided in § <a href="http://law.lis.virginia.gov/vacode/19.2-386.29/" target="_blank" rel="noreferrer">19.2-386.29</a>, whenever a firearm is identified as stolen, the law-enforcement agency shall return such firearm to the rightful owner thereof, if known, provided the owner is not prohibited from possessing the firearm and the agency does not need to retain the firearm as evidence in a criminal prosecution.</p><p>1993, cc. 475, 834; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0394" target="_blank" rel="noreferrer">394</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0214" target="_blank" rel="noreferrer">214</a>.</p>',5),o=[i];function s(c,f,l,h,d,m){return r(),a("div",null,o)}const u=e(n,[["render",s]]);export{g as __pageData,u as default};
