import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 52-34.4 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-34.4.md","filePath":"vacode/52-34.4.md"}'),o={name:"vacode/52-34.4.md"},n=i('<h1 id="§-52-34-4-definitions" tabindex="-1">§ 52-34.4 Definitions. <a class="header-anchor" href="#§-52-34-4-definitions" aria-label="Permalink to &quot;§ 52-34.4 Definitions.&quot;">​</a></h1><p>As used in this chapter:</p><p>&quot;Media&quot; means print, radio, television, and Internet-based communication systems or other methods of communicating information to the public.</p><p>&quot;Missing senior adult&quot; means an adult whose whereabouts are unknown and who is over 60 years of age and suffers a cognitive impairment to the extent that he is unable to provide care to himself without assistance from a caregiver, including a diagnosis of Alzheimer&#39;s Disease or dementia, and whose disappearance poses a credible threat as determined by a law-enforcement agency to the health and safety of the adult and under such other circumstances as deemed appropriate by the Virginia State Police.</p><p>&quot;Senior alert&quot; means the notice of a missing senior adult provided to the public by the media or other methods under a Senior Alert Agreement.</p><p>&quot;Senior Alert Agreement&quot; means a voluntary agreement between law-enforcement officials and members of the media whereby a senior adult will be declared missing, and the public will be notified by media outlets, and includes all other incidental conditions of the partnership as found appropriate by the Virginia State Police.</p><p>&quot;Senior Alert Program&quot; or &quot;Program&quot; means the procedures and Senior Alert Agreements to aid in the identification and location of a missing senior adult.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0486" target="_blank" rel="noreferrer">486</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0723" target="_blank" rel="noreferrer">723</a>.</p>',8),r=[n];function s(d,c,l,h,p,m){return t(),a("div",null,r)}const _=e(o,[["render",s]]);export{f as __pageData,_ as default};
