import{_ as a,o as r,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 52-34.8 Establishment of the Virginia Blue Alert Program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-34.8.md","filePath":"vacode/52-34.8.md"}'),i={name:"vacode/52-34.8.md"},n=e("h1",{id:"§-52-34-8-establishment-of-the-virginia-blue-alert-program",tabindex:"-1"},[t("§ 52-34.8 Establishment of the Virginia Blue Alert Program. "),e("a",{class:"header-anchor",href:"#§-52-34-8-establishment-of-the-virginia-blue-alert-program","aria-label":'Permalink to "§ 52-34.8 Establishment of the Virginia Blue Alert Program."'},"​")],-1),s=e("p",null,"The Department of State Police shall establish a Blue Alert Program in the Commonwealth and develop policies for its implementation. The Blue Alert Program may be activated when a suspect for a crime involving the death or serious injury of a law-enforcement officer has not been apprehended and may be a serious threat to the public or when a law-enforcement officer is missing while in the line of duty under circumstances warranting concern for the law-enforcement officer's safety. The Department of State Police shall (i) establish procedures and standards by which a local law-enforcement agency may assess whether the conditions for a Blue Alert have been met and report such information to the Department of State Police, (ii) inform local law-enforcement officials of the policies and procedures for the Blue Alert Program set by the Department, and (iii) assist in determining the geographic scope of a particular Blue Alert.",-1),l=e("p",null,[t("2011, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0669",target:"_blank",rel:"noreferrer"},"669"),t(".")],-1),c=[n,s,l];function h(m,f,d,p,u,g){return r(),o("div",null,c)}const v=a(i,[["render",h]]);export{b as __pageData,v as default};
