import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 52-41 Receipt of information; immunity from liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-41.md","filePath":"vacode/52-41.md"}'),r={name:"vacode/52-41.md"},n=o('<h1 id="§-52-41-receipt-of-information-immunity-from-liability" tabindex="-1">§ 52-41 Receipt of information; immunity from liability. <a class="header-anchor" href="#§-52-41-receipt-of-information-immunity-from-liability" aria-label="Permalink to &quot;§ 52-41 Receipt of information; immunity from liability.&quot;">​</a></h1><p>A. Any insurer providing information to an authorized representative of the Department pursuant to § <a href="http://law.lis.virginia.gov/vacode/52-38/" target="_blank" rel="noreferrer">52-38</a> or pursuant to subdivision B 5 of § <a href="http://law.lis.virginia.gov/vacode/38.2-613/" target="_blank" rel="noreferrer">38.2-613</a> shall have the right to request relevant information and receive, within thirty days, the information requested.</p><p>B. No cause of action in the nature of defamation, invasion of privacy, or negligence shall arise against any person furnishing information concerning any suspected, anticipated or completed criminal violation when the information is provided to or received from the Department, the National Association of Insurance Commissioners, another insurer, any federal or state governmental entity established for the purposes of detecting and preventing insurance fraud, or the National Insurance Crime Bureau.</p><p>C. No insurer, its officers or employees, insurance professional or any other person shall be subject to such cause of action for cooperating with, or furnishing evidence or information regarding any suspected criminal violation to the Department.</p><p>D. This section shall not provide immunity for those disclosing or furnishing false information with malice or willful intent to injure any person.</p><p>E. This section does not abrogate or modify in any way common law or statutory privilege or immunity heretofore enjoyed by any person or entity, nor does it authorize the Department to make public insurance company records that are proprietary in nature.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0371" target="_blank" rel="noreferrer">371</a>.</p>',7),a=[n];function s(l,c,f,p,m,h){return i(),t("div",null,a)}const g=e(r,[["render",s]]);export{d as __pageData,g as default};
