import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 52-54 Office of the Gaming Enforcement Coordinator established; purpose; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-54.md","filePath":"vacode/52-54.md"}'),n={name:"vacode/52-54.md"},i=o('<h1 id="§-52-54-office-of-the-gaming-enforcement-coordinator-established-purpose-duties" tabindex="-1">§ 52-54 Office of the Gaming Enforcement Coordinator established; purpose; duties. <a class="header-anchor" href="#§-52-54-office-of-the-gaming-enforcement-coordinator-established-purpose-duties" aria-label="Permalink to &quot;§ 52-54 Office of the Gaming Enforcement Coordinator established; purpose; duties.&quot;">​</a></h1><p>A. The Superintendent shall designate a Department employee to serve as the Gaming Enforcement Coordinator. The purpose of the office of the Coordinator shall be to synchronize the enforcement of gaming laws by state and local law enforcement, and to serve as a liaison between such agencies and federal law enforcement.</p><p>B. The Coordinator shall have the following duties:</p><ol><li><p>Coordinating enforcement of the Commonwealth&#39;s gaming laws by the Department, the Department of Agriculture and Consumer Services, and all other state agencies; attorneys for the Commonwealth; and local law enforcement;</p></li><li><p>Acting as a liaison between the federal government and the agencies identified in subdivision 1 for purposes of any federal investigation into gaming activities;</p></li><li><p>Establishing, advertising, and administering a tip line, which may be accessed by phone and by Internet, for members of the public to report concerns about, or suspected instances of, gaming activities; and</p></li><li><p>Performing any other duties as are necessary to promote and enable the equitable enforcement of gaming laws in the Commonwealth.</p></li></ol><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0768" target="_blank" rel="noreferrer">768</a>.</p>',5),r=[i];function s(l,c,d,f,h,p){return t(),a("div",null,r)}const u=e(n,[["render",s]]);export{g as __pageData,u as default};
