import{_ as t,o,c as a,z as e,a as r}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 52-8.1 Powers and duties of Bureau of Criminal Investigation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-8.1.md","filePath":"vacode/52-8.1.md"}'),n={name:"vacode/52-8.1.md"},i=e("h1",{id:"§-52-8-1-powers-and-duties-of-bureau-of-criminal-investigation",tabindex:"-1"},[r("§ 52-8.1 Powers and duties of Bureau of Criminal Investigation. "),e("a",{class:"header-anchor",href:"#§-52-8-1-powers-and-duties-of-bureau-of-criminal-investigation","aria-label":'Permalink to "§ 52-8.1 Powers and duties of Bureau of Criminal Investigation."'},"​")],-1),s=e("p",null,"In addition to any other powers and duties which may be provided by statute or otherwise, the Bureau of Criminal Investigation shall conduct an investigation into any matter referred to it by the Governor. The Bureau of Criminal Investigation shall, when requested by the Attorney General, any sheriff, chief of police, attorney for the Commonwealth or grand jury, conduct an investigation into the subject matter so directed or requested, if the person or grand jury making the request has reasonable cause to believe the acts committed or about to be committed would constitute a Class 1, 2 or 3 felony, to determine whether any such criminal violations have occurred, are occurring or are about to occur. The Bureau may, in all other requests for investigations made by the Attorney General, any sheriff, chief of police, attorney for the Commonwealth or grand jury, conduct an investigation into the subject matter so requested, to determine whether any criminal violations have occurred, are occurring or are about to occur.",-1),c=e("p",null,"1974, c. 483; 1975, c. 650; 1980, c. 232.",-1),d=[i,s,c];function u(h,l,m,f,v,_){return o(),a("div",null,d)}const g=t(n,[["render",u]]);export{y as __pageData,g as default};
