import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 52-8.5 Reporting hate crimes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/52-8.5.md","filePath":"vacode/52-8.5.md"}'),a={name:"vacode/52-8.5.md"},n=r('<h1 id="§-52-8-5-reporting-hate-crimes" tabindex="-1">§ 52-8.5 Reporting hate crimes. <a class="header-anchor" href="#§-52-8-5-reporting-hate-crimes" aria-label="Permalink to &quot;§ 52-8.5 Reporting hate crimes.&quot;">​</a></h1><p>A. The Superintendent shall establish and maintain within the Department of State Police a central repository for the collection and analysis of information regarding hate crimes and groups and individuals carrying out such acts.</p><p>B. State, county, and municipal law-enforcement agencies shall report to the Department all hate crimes occurring in their jurisdictions in a form, time, and manner prescribed by the Superintendent. Such reports shall not be open to public inspection except insofar as the Superintendent shall permit.</p><p>C. As used in this section:</p><p>&quot;Disability&quot; means a physical or mental impairment that substantially limits one or more of a person&#39;s major life activities.</p><p>&quot;Hate crime&quot; means (i) a criminal act committed against a person or his property with the specific intent of instilling fear or intimidation in the individual against whom the act is perpetrated because of race, religion, gender, disability, gender identity, sexual orientation, or ethnic or national origin or that is committed for the purpose of restraining that person from exercising his rights under the Constitution or laws of the Commonwealth or of the United States; (ii) any illegal act directed against any persons or their property because of those persons&#39; race, religion, gender, disability, gender identity, sexual orientation, or ethnic or national origin; and (iii) all other incidents, as determined by law-enforcement authorities, intended to intimidate or harass any individual or group because of race, religion, gender, disability, gender identity, sexual orientation, or ethnic or national origin.</p><p>1988, c. 838; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0124" target="_blank" rel="noreferrer">124</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0746" target="_blank" rel="noreferrer">746</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1171" target="_blank" rel="noreferrer">1171</a>.</p>',7),o=[n];function s(l,c,p,h,d,g){return i(),t("div",null,o)}const u=e(a,[["render",s]]);export{f as __pageData,u as default};
