import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse(`{"title":"§ 53.1-131.3 Payment of costs associated with prisoners' keep.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-131.3.md","filePath":"vacode/53.1-131.3.md"}`),s={name:"vacode/53.1-131.3.md"},o=r('<h1 id="§-53-1-131-3-payment-of-costs-associated-with-prisoners-keep" tabindex="-1">§ 53.1-131.3 Payment of costs associated with prisoners&#39; keep. <a class="header-anchor" href="#§-53-1-131-3-payment-of-costs-associated-with-prisoners-keep" aria-label="Permalink to &quot;§ 53.1-131.3 Payment of costs associated with prisoners&#39; keep.&quot;">​</a></h1><p>Any sheriff or jail superintendent may establish a program to charge inmates a reasonable fee, not to exceed $3 per day, to defray the costs associated with the prisoners&#39; keep. The Board shall develop a model plan and adopt regulations for such program, and shall provide assistance, if requested, to the sheriff or jail superintendent in the implementation of such program. Such funds shall be retained in the locality where the funds were collected and shall be used for general jail purposes; however, in the event the jail is a regional jail, funds collected from any such fee shall be retained by the regional jail. Any person jailed for an offense they are later acquitted for shall be refunded any such fees paid during their incarceration.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0548" target="_blank" rel="noreferrer">548</a>.</p>',3),i=[o];function n(l,c,d,h,p,f){return a(),t("div",null,i)}const g=e(s,[["render",n]]);export{u as __pageData,g as default};
