import{_ as a,o as t,c as o,z as e,a as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 53.1-133 Treatment of prisoner with contagious disease.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-133.md","filePath":"vacode/53.1-133.md"}'),s={name:"vacode/53.1-133.md"},n=e("h1",{id:"§-53-1-133-treatment-of-prisoner-with-contagious-disease",tabindex:"-1"},[i("§ 53.1-133 Treatment of prisoner with contagious disease. "),e("a",{class:"header-anchor",href:"#§-53-1-133-treatment-of-prisoner-with-contagious-disease","aria-label":'Permalink to "§ 53.1-133 Treatment of prisoner with contagious disease."'},"​")],-1),r=e("p",null,"Upon application of the person in charge of a local correctional facility, if that application is affirmed by the physician serving such facility, a judge of a circuit court is authorized to have removed from any correctional facility within his jurisdiction any person confined therein who has contracted any contagious or infectious disease dangerous to the public health. Such persons shall be removed to some other place designated by the judge. When any person is so removed, he shall be safely kept and receive proper care and attention including medical treatment. As soon as he recovers his health, he shall be returned to the correctional facility from which he was moved, unless the term of his imprisonment has expired, in which event he shall be discharged, but not until all danger of his spreading contagion has passed. Expenses incurred under and by reason of this section shall be paid as provided by law.",-1),c=e("p",null,"Code 1950, § 53-135.2; 1979, c. 109; 1982, c. 636.",-1),h=[n,r,c];function d(l,p,f,u,m,_){return t(),o("div",null,h)}const y=a(s,[["render",d]]);export{v as __pageData,y as default};
