import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 53.1-165.1 Limitation on the application of parole statutes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-165.1.md","filePath":"vacode/53.1-165.1.md"}'),r={name:"vacode/53.1-165.1.md"},n=t('<h1 id="§-53-1-165-1-limitation-on-the-application-of-parole-statutes" tabindex="-1">§ 53.1-165.1 Limitation on the application of parole statutes. <a class="header-anchor" href="#§-53-1-165-1-limitation-on-the-application-of-parole-statutes" aria-label="Permalink to &quot;§ 53.1-165.1 Limitation on the application of parole statutes.&quot;">​</a></h1><p>A. The provisions of this article, except §§ <a href="/vacode/53.1-160/">53.1-160</a> and <a href="/vacode/53.1-160.1/">53.1-160.1</a>, shall not apply to any sentence imposed or to any prisoner incarcerated upon a conviction for a felony offense committed on or after January 1, 1995. Any person sentenced to a term of incarceration for a felony offense committed on or after January 1, 1995, shall not be eligible for parole upon that offense.</p><p>B. The provisions of this article shall apply to any person who was sentenced by a jury prior to June 9, 2000, for any felony offense committed on or after January 1, 1995, and who remained incarcerated for such offense on July 1, 2020, other than (i) a Class 1 felony or (ii) any of the following felony offenses where the victim was a minor: (a) rape in violation of § <a href="/vacode/18.2-61/">18.2-61</a>; (b) forcible sodomy in violation of § <a href="/vacode/18.2-67.1/">18.2-67.1</a>; (c) object sexual penetration in violation of § <a href="/vacode/18.2-67.2/">18.2-67.2</a>; (d) aggravated sexual battery in violation of § <a href="/vacode/18.2-67.3/">18.2-67.3</a>; (e) an attempt to commit a violation of clause (a), (b), (c), or (d); or (f) carnal knowledge in violation of § <a href="/vacode/18.2-63/">18.2-63</a>, <a href="/vacode/18.2-64.1/">18.2-64.1</a>, or <a href="/vacode/18.2-64.2/">18.2-64.2</a>.</p><p>C. The Parole Board shall establish procedures for consideration of parole of persons entitled under subsection B consistent with the provisions of § <a href="/vacode/53.1-154/">53.1-154</a>.</p><p>D. Any person who meets eligibility criteria for parole under subsection B and pursuant to § <a href="/vacode/53.1-151/">53.1-151</a> as of July 1, 2020, shall be scheduled for a parole interview no later than July 1, 2021, allowing for extension of time for reasonable cause.</p><p>E. Notwithstanding the provisions of subsection A or any other provision of this article to the contrary, any person sentenced to a term of life imprisonment for a single felony or multiple felonies committed while the person was a juvenile and who has served at least 20 years of such sentence shall be eligible for parole and any person who has active sentences that total more than 20 years for a single felony or multiple felonies committed while the person was a juvenile and who has served at least 20 years of such sentences shall be eligible for parole. The Board shall review and decide the case of each prisoner who is eligible for parole in accordance with § <a href="/vacode/53.1-154/">53.1-154</a> and rules adopted pursuant to subdivision 2 of § <a href="/vacode/53.1-136/">53.1-136</a>.</p><p>1994, 2nd Sp. Sess., cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1200" target="_blank" rel="noreferrer">1200</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1272" target="_blank" rel="noreferrer">1272</a>.</p>',7),i=[n];function l(s,f,c,h,p,d){return a(),o("div",null,i)}const g=e(r,[["render",l]]);export{v as __pageData,g as default};
