import{_ as e,o,c as a,O as n}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 53.1-189 Forfeiture and restoration of good conduct allowance and earned sentence credits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-189.md","filePath":"vacode/53.1-189.md"}'),r={name:"vacode/53.1-189.md"},t=n('<h1 id="§-53-1-189-forfeiture-and-restoration-of-good-conduct-allowance-and-earned-sentence-credits" tabindex="-1">§ 53.1-189 Forfeiture and restoration of good conduct allowance and earned sentence credits. <a class="header-anchor" href="#§-53-1-189-forfeiture-and-restoration-of-good-conduct-allowance-and-earned-sentence-credits" aria-label="Permalink to &quot;§ 53.1-189 Forfeiture and restoration of good conduct allowance and earned sentence credits.&quot;">​</a></h1><p>A. Except for credits allowed under § <a href="/vacode/53.1-191/">53.1-191</a>, all or any part of a person&#39;s accrued good conduct allowance and earned sentence credits earned after admission to a state correctional facility on any sentence or combination of sentences being served may be forfeited in accordance with rules and regulations of the Director for violation of any written prison rules or regulations.</p><p>B. If a prisoner is convicted of escape or attempted escape from any correctional facility, such person shall, upon being returned to custody, forfeit all accrued good conduct allowance and all earned sentence credits on any sentence or combination of sentences being served, except for credits allowed under § <a href="/vacode/53.1-191/">53.1-191</a>.</p><p>C. No good conduct allowance or earned sentence credit which has been forfeited shall be restored except by the Director, whose authority shall not be delegated.</p><p>Code 1950, § 53-214; 1974, c. 69; 1980, c. 485; 1981, c. 392; 1982, c. 636; 1984, c. 734; 1994, 2nd Sp. Sess., cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0759" target="_blank" rel="noreferrer">759</a>.</p>',5),c=[t];function d(i,s,l,f,p,u){return o(),a("div",null,c)}const g=e(r,[["render",d]]);export{h as __pageData,g as default};
