import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-198 Certain persons to choose good conduct system.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-198.md","filePath":"vacode/53.1-198.md"}'),r={name:"vacode/53.1-198.md"},s=a('<h1 id="§-53-1-198-certain-persons-to-choose-good-conduct-system" tabindex="-1">§ 53.1-198 Certain persons to choose good conduct system. <a class="header-anchor" href="#§-53-1-198-certain-persons-to-choose-good-conduct-system" aria-label="Permalink to &quot;§ 53.1-198 Certain persons to choose good conduct system.&quot;">​</a></h1><p>Every person who, on or before June 30, 1981, was convicted of a felony and every person convicted of a misdemeanor, and to whom the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/53.1-151/" target="_blank" rel="noreferrer">53.1-151</a>, <a href="http://law.lis.virginia.gov/vacode/53.1-152/" target="_blank" rel="noreferrer">53.1-152</a> or § <a href="http://law.lis.virginia.gov/vacode/53.1-153/" target="_blank" rel="noreferrer">53.1-153</a> apply, may choose the system of good conduct allowances established in §§ <a href="http://law.lis.virginia.gov/vacode/53.1-199/" target="_blank" rel="noreferrer">53.1-199</a> through <a href="http://law.lis.virginia.gov/vacode/53.1-202/" target="_blank" rel="noreferrer">53.1-202</a> to govern the computing of his discharge date and eligibility for parole. A person who chooses the system established in this article may not thereafter be governed by the laws establishing good conduct allowances in effect prior to July 1, 1981.</p><p>Code 1950, § 53-209.1; 1981, c. 392; 1982, c. 636.</p>',3),n=[s];function c(i,d,l,h,p,_){return o(),t("div",null,n)}const v=e(r,[["render",c]]);export{g as __pageData,v as default};
