import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 53.1-20.1 Compensation of local jails for cost of incarceration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-20.1.md","filePath":"vacode/53.1-20.1.md"}'),r={name:"vacode/53.1-20.1.md"},i=a('<h1 id="§-53-1-20-1-compensation-of-local-jails-for-cost-of-incarceration" tabindex="-1">§ 53.1-20.1 Compensation of local jails for cost of incarceration. <a class="header-anchor" href="#§-53-1-20-1-compensation-of-local-jails-for-cost-of-incarceration" aria-label="Permalink to &quot;§ 53.1-20.1 Compensation of local jails for cost of incarceration.&quot;">​</a></h1><p>If the Director is unable to accommodate in a state correctional facility any convicted felon sentenced to the Department for a felony committed before January 1, 1995, whose sentence totals more than two years or who is convicted of a felony committed on or after January 1, 1995, and who is required to serve a total period of one year or more in a state correctional facility, the Department of Corrections shall compensate local jails for the cost of incarceration as provided for in the general appropriation act beginning on the sixty-first day following the date of mailing by certified letter or electronic transmittal by the clerk of the committing court to the Director of the final order.</p><p>1982, c. 680; 1990, cc. 676, 768; 1994, 2nd Sp. Sess., cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?943+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?943+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0775" target="_blank" rel="noreferrer">775</a>.</p>',3),n=[i];function c(l,s,f,d,p,h){return o(),t("div",null,n)}const g=e(r,[["render",c]]);export{m as __pageData,g as default};
