import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-203 Felonies by prisoners; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-203.md","filePath":"vacode/53.1-203.md"}'),n={name:"vacode/53.1-203.md"},t=i('<h1 id="§-53-1-203-felonies-by-prisoners-penalties" tabindex="-1">§ 53.1-203 Felonies by prisoners; penalties. <a class="header-anchor" href="#§-53-1-203-felonies-by-prisoners-penalties" aria-label="Permalink to &quot;§ 53.1-203 Felonies by prisoners; penalties.&quot;">​</a></h1><p>It shall be unlawful for a prisoner in a state, local or community correctional facility or in the custody of an employee thereof to:</p><ol><li><p>Escape from a correctional facility or from any person in charge of such prisoner;</p></li><li><p>Willfully break, cut or damage any building, furniture, fixture or fastening of such facility or any part thereof for the purpose of escaping, aiding any other prisoner to escape therefrom or rendering such facility less secure as a place of confinement;</p></li><li><p>Make, procure, secrete or have in his possession any instrument, tool or other thing for the purpose of escaping from or aiding another to escape from a correctional facility or employee thereof;</p></li><li><p>Make, procure, secrete or have in his possession a knife, instrument, tool or other thing not authorized by the superintendent or sheriff which is capable of causing death or bodily injury;</p></li><li><p>Procure, sell, secrete or have in his possession any chemical compound which he has not lawfully received;</p></li><li><p>Procure, sell, secrete or have in his possession a controlled substance classified in Schedule III of the Drug Control Act (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.) or marijuana;</p></li><li><p>Introduce into a correctional facility or have in his possession firearms or ammunition for firearms;</p></li><li><p>Willfully burn or destroy by use of any explosive device or substance, in whole or in part, or cause to be so burned or destroyed, any personal property, within any correctional facility;</p></li><li><p>Willfully tamper with, damage, destroy, or disable any fire protection or fire suppression system, equipment, or sprinklers within any correctional facility; or</p></li><li><p>Conspire with another prisoner or other prisoners to commit any of the foregoing acts.</p></li></ol><p>For violation of any of the provisions of this section, except subdivision 6, the prisoner shall be guilty of a Class 6 felony. For a violation of subdivision 6, he shall be guilty of a Class 5 felony. If the violation is of subdivision 1 of this section and the escapee is a felon, he shall be sentenced to a mandatory minimum term of confinement of one year, which shall be served consecutively with any other sentence. The prisoner shall, upon conviction of escape, immediately commence to serve such escape sentence, and he shall not be eligible for parole during such period. Any prisoner sentenced to life imprisonment who escapes shall not be eligible for parole. No part of the time served for escape shall be credited for the purpose of parole toward the sentence or sentences, the service of which is interrupted for service of the escape sentence, nor shall it be credited for such purpose toward any other sentence.</p><p>Code 1950, § 53-291; 1966, c. 300; 1970, c. 648; 1973, c. 403; 1975, c. 588; 1977, c. 497; 1978, cc. 177, 361; 1982, c. 636; 1985, c. 555; 1988, c. 371; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0104" target="_blank" rel="noreferrer">104</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>.</p>',5),a=[t];function s(l,c,p,h,f,u){return o(),r("div",null,a)}const y=e(n,[["render",s]]);export{g as __pageData,y as default};
