import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 53.1-265 Powers and duties not delegable to contractor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-265.md","filePath":"vacode/53.1-265.md"}'),r={name:"vacode/53.1-265.md"},a=t('<h1 id="§-53-1-265-powers-and-duties-not-delegable-to-contractor" tabindex="-1">§ 53.1-265 Powers and duties not delegable to contractor. <a class="header-anchor" href="#§-53-1-265-powers-and-duties-not-delegable-to-contractor" aria-label="Permalink to &quot;§ 53.1-265 Powers and duties not delegable to contractor.&quot;">​</a></h1><p>No contract for correctional services shall authorize, allow, or imply a delegation of authority or responsibility of the Director to a prison contractor for any of the following:</p><ol><li><p>Developing and implementing procedures for calculating inmate release and parole eligibility dates;</p></li><li><p>Developing and implementing procedures for calculating and awarding sentence credits;</p></li><li><p>Approving inmates for furlough and work release;</p></li><li><p>Approving the type of work inmates may perform and the wages or sentence credits which may be given the inmates engaging in such work;</p></li><li><p>Granting, denying, or revoking sentence credits;</p></li><li><p>Classifying inmates or placing inmates in less restrictive custody or more restrictive custody;</p></li><li><p>Transferring an inmate; however, the contractor may make written recommendations regarding the transfer of an inmate or inmates;</p></li><li><p>Formulating rules of inmate behavior, violations of which may subject inmates to sanctions; however, the contractor may propose such rules to the Director for his review and adoption, rejection, or modification as otherwise provided by law or regulation; and</p></li><li><p>Disciplining inmates in any manner which requires a discretionary application of rules of inmate behavior or a discretionary imposition of a sanction for violations of such rules.</p></li></ol><p>1991, c. 705; 1992, c. 654; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>.</p>',4),n=[a];function s(l,c,p,d,m,h){return o(),i("div",null,n)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
