import{_ as t,o as a,c as o,z as e,a as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 53.1-28 Authority to fix discharge date; improper release; warrant, arrest and hearing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-28.md","filePath":"vacode/53.1-28.md"}'),i={name:"vacode/53.1-28.md"},s=e("h1",{id:"§-53-1-28-authority-to-fix-discharge-date-improper-release-warrant-arrest-and-hearing",tabindex:"-1"},[r("§ 53.1-28 Authority to fix discharge date; improper release; warrant, arrest and hearing. "),e("a",{class:"header-anchor",href:"#§-53-1-28-authority-to-fix-discharge-date-improper-release-warrant-arrest-and-hearing","aria-label":'Permalink to "§ 53.1-28 Authority to fix discharge date; improper release; warrant, arrest and hearing."'},"​")],-1),n=e("p",null,"For the purpose of scheduling and providing a uniform, effective and continual program of pre-release training and conditioning of prisoners, the Director shall have authority to discharge any prisoner within the Virginia penal system on any day within a period of 30 days prior to the date upon which such prisoner's term would normally expire. The Director shall provide each prisoner with the following documents upon discharge: (i) verification of the prisoner's work history while in custody; (ii) certification of all educational and treatment programs completed by the prisoner while in custody; and (iii) a copy of his medical records, so long as such prisoner requests a copy of his records at least 60 days prior to the date upon which the prisoner's term would expire. The Department shall develop procedures wherein the records are to be made available to the prisoner in a safe and secure manner.",-1),h=e("p",null,"The Director or his designee upon the discovery of an improper release or discharge of a prisoner from custody shall report such release or discharge to the circuit court of the jurisdiction wherein the prisoner was released or discharged. The circuit court shall then issue a warrant for the arrest of the prisoner which may be executed by any duly sworn correctional officer or law-enforcement officer. Such warrant shall direct that the prisoner be presented forthwith to the court to determine the propriety of the original discharge or release. After a hearing, if the court is satisfied that the release or discharge was made improperly, the prisoner shall be returned to the state correctional facility from which he was released or discharged, or to any other correctional facility designated by the Director to serve the remainder of his sentence.",-1),c=e("p",null,[r("Code 1950, § 53-37; 1964, c. 140; 1968, c. 303; 1982, c. 636; 2006, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0108",target:"_blank",rel:"noreferrer"},"108"),r(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0132",target:"_blank",rel:"noreferrer"},"132"),r(".")],-1),d=[s,n,h,c];function l(p,f,u,g,m,y){return a(),o("div",null,d)}const v=t(i,[["render",l]]);export{_ as __pageData,v as default};
