import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-40.01 Conditional release of geriatric prisoners.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-40.01.md","filePath":"vacode/53.1-40.01.md"}'),t={name:"vacode/53.1-40.01.md"},i=o('<h1 id="§-53-1-40-01-conditional-release-of-geriatric-prisoners" tabindex="-1">§ 53.1-40.01 Conditional release of geriatric prisoners. <a class="header-anchor" href="#§-53-1-40-01-conditional-release-of-geriatric-prisoners" aria-label="Permalink to &quot;§ 53.1-40.01 Conditional release of geriatric prisoners.&quot;">​</a></h1><p>Any person serving a sentence imposed upon a conviction for a felony offense, other than a Class 1 felony, (i) who has reached the age of sixty-five or older and who has served at least five years of the sentence imposed or (ii) who has reached the age of sixty or older and who has served at least ten years of the sentence imposed may petition the Parole Board for conditional release. The Parole Board shall promulgate regulations to implement the provisions of this section.</p><p>1994, 2nd Sp. Sess., cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?943+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?943+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0487" target="_blank" rel="noreferrer">487</a>.</p>',3),n=[i];function s(l,c,h,d,f,p){return r(),a("div",null,n)}const v=e(t,[["render",s]]);export{g as __pageData,v as default};
