import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 53.1-40.7 Discharge of prisoner involuntarily admitted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-40.7.md","filePath":"vacode/53.1-40.7.md"}'),r={name:"vacode/53.1-40.7.md"},o=a('<h1 id="§-53-1-40-7-discharge-of-prisoner-involuntarily-admitted" tabindex="-1">§ 53.1-40.7 Discharge of prisoner involuntarily admitted. <a class="header-anchor" href="#§-53-1-40-7-discharge-of-prisoner-involuntarily-admitted" aria-label="Permalink to &quot;§ 53.1-40.7 Discharge of prisoner involuntarily admitted.&quot;">​</a></h1><p>A. The prisoner shall be discharged from a hospital or facility for the care and treatment of individuals with mental illness to a state or local correctional facility designated by the Director if there is no further need for involuntary hospitalization or at the expiration of 180 days unless involuntarily committed by further petition and order of a court as provided herein.</p><p>B. Notwithstanding the provisions of subsection A, if there is no further need for involuntary hospitalization, the prisoner may be retained in such hospital or facility if the prisoner (i) is capable of and consents to voluntary admission, and (ii) has been examined by a licensed physician, psychiatrist, or clinical psychologist acting on staff within his area of expertise and is determined to be in need of continued hospitalization.</p><p>1988, c. 873; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0639" target="_blank" rel="noreferrer">639</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',4),n=[o];function s(l,c,d,h,f,p){return i(),t("div",null,n)}const g=e(r,[["render",s]]);export{u as __pageData,g as default};
