import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 53.1-45.1 Work programs; agreements with other entities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-45.1.md","filePath":"vacode/53.1-45.1.md"}'),o={name:"vacode/53.1-45.1.md"},i=a('<h1 id="§-53-1-45-1-work-programs-agreements-with-other-entities" tabindex="-1">§ 53.1-45.1 Work programs; agreements with other entities. <a class="header-anchor" href="#§-53-1-45-1-work-programs-agreements-with-other-entities" aria-label="Permalink to &quot;§ 53.1-45.1 Work programs; agreements with other entities.&quot;">​</a></h1><p>A. The Director, with the prior approval of the Governor, may enter into an agreement with a public or private entity to operate a work program in a state correctional facility for prisoners confined therein.</p><p>B. Articles produced or manufactured and services provided by prisoners participating in such a program may be purchased as provided in § <a href="http://law.lis.virginia.gov/vacode/53.1-47/" target="_blank" rel="noreferrer">53.1-47</a> and may be bought, sold or acquired by exchange on the open market through the participating public or private entity.</p><p>C. The Director shall arrange for compensation for such employment. Wages earned by prisoners shall be paid to the Director who shall deduct from such wages, in the following order of priority, an amount to:</p><ol><li><p>Meet the obligation of any judicial or administrative order to provide support and such funds shall be disbursed according to the terms of such order;</p></li><li><p>Pay any fines, restitution or costs as ordered by the court; and</p></li><li><p>Defray a portion of the prisoner&#39;s keep.</p></li></ol><p>The balance shall be credited to the prisoner&#39;s account in accordance with § <a href="http://law.lis.virginia.gov/vacode/53.1-42/" target="_blank" rel="noreferrer">53.1-42</a>.</p><p>1993, cc. 464, 488; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0284" target="_blank" rel="noreferrer">284</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0368" target="_blank" rel="noreferrer">368</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0094" target="_blank" rel="noreferrer">94</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>.</p>',7),n=[i];function s(p,c,l,h,d,g){return r(),t("div",null,n)}const m=e(o,[["render",s]]);export{_ as __pageData,m as default};
