import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 53.1-52 Procedure for purchases.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-52.md","filePath":"vacode/53.1-52.md"}'),t={name:"vacode/53.1-52.md"},i=o('<h1 id="§-53-1-52-procedure-for-purchases" tabindex="-1">§ 53.1-52 Procedure for purchases. <a class="header-anchor" href="#§-53-1-52-procedure-for-purchases" aria-label="Permalink to &quot;§ 53.1-52 Procedure for purchases.&quot;">​</a></h1><p>All purchases, except for those of information technology and telecommunications goods and services as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2012/" target="_blank" rel="noreferrer">2.2-2012</a>, made by departments, institutions and agencies of the Commonwealth shall be made as provided by the Division of Purchases and Supply of the Department of General Services. All purchases of information technology and telecommunications made by departments, institutions, and agencies of the Commonwealth shall be made as provided by the Virginia Information Technologies Agency. All other purchases shall be upon requisition by the proper authority of the county, district, city or town requiring such articles.</p><p>Code 1950, § 53-68; 1958, c. 124; 1982, cc. 636, 647; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>.</p>',3),n=[i];function s(c,l,h,d,p,f){return r(),a("div",null,n)}const g=e(t,[["render",s]]);export{_ as __pageData,g as default};
