import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 53.1-61 Determination whether prisoner has dependents receiving public assistance; payment of portion of earnings; remedies for enforcement of support obligation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-61.md","filePath":"vacode/53.1-61.md"}'),i={name:"vacode/53.1-61.md"},n=t('<h1 id="§-53-1-61-determination-whether-prisoner-has-dependents-receiving-public-assistance-payment-of-portion-of-earnings-remedies-for-enforcement-of-support-obligation" tabindex="-1">§ 53.1-61 Determination whether prisoner has dependents receiving public assistance; payment of portion of earnings; remedies for enforcement of support obligation. <a class="header-anchor" href="#§-53-1-61-determination-whether-prisoner-has-dependents-receiving-public-assistance-payment-of-portion-of-earnings-remedies-for-enforcement-of-support-obligation" aria-label="Permalink to &quot;§ 53.1-61 Determination whether prisoner has dependents receiving public assistance; payment of portion of earnings; remedies for enforcement of support obligation.&quot;">​</a></h1><p>A. In order to determine whether a prisoner to be released for employment as provided in § <a href="http://law.lis.virginia.gov/vacode/53.1-60/" target="_blank" rel="noreferrer">53.1-60</a> has dependents receiving public assistance benefits, the Director may require such person to reveal the identity and residence of any dependents as a condition to release. The Director shall notify any such dependents, the local department of social services where such dependents reside and the Commissioner of Social Services of the release of such person for employment. Upon request of the local department of social services or the Commissioner of Social Services, the Director shall withhold and pay over a portion of the person&#39;s earnings as provided in § <a href="http://law.lis.virginia.gov/vacode/53.1-60/" target="_blank" rel="noreferrer">53.1-60</a>.</p><p>B. If the local department of social services or the Commissioner of Social Services objects to the amount withheld by the Director, the balance credited to the person&#39;s account shall be subject to all civil remedies provided by law to the local department of social services or the Commissioner of Social Services for the enforcement of support of dependents receiving public assistance benefits.</p><p>C. The director of the local department of social services and the Commissioner of Social Services or their designees shall be permitted access to the records of the Director concerning the earnings of the prisoner.</p><p>Code 1950, § 53-38.01; 1978, c. 660; 1982, c. 636; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',5),s=[n];function a(c,p,d,l,h,f){return o(),r("div",null,s)}const g=e(i,[["render",a]]);export{v as __pageData,g as default};
