import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-63 Department to establish facilities for persons committed under Article 2 (§ 19.2-311 et seq.) of Chapter 18 of Title 19.2.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-63.md","filePath":"vacode/53.1-63.md"}'),r={name:"vacode/53.1-63.md"},n=a('<h1 id="§-53-1-63-department-to-establish-facilities-for-persons-committed-under-article-2-§-19-2-311-et-seq-of-chapter-18-of-title-19-2" tabindex="-1">§ 53.1-63 Department to establish facilities for persons committed under Article 2 (§ 19.2-311 et seq.) of Chapter 18 of Title 19.2. <a class="header-anchor" href="#§-53-1-63-department-to-establish-facilities-for-persons-committed-under-article-2-§-19-2-311-et-seq-of-chapter-18-of-title-19-2" aria-label="Permalink to &quot;§ 53.1-63 Department to establish facilities for persons committed under Article 2 (§ 19.2-311 et seq.) of Chapter 18 of Title 19.2.&quot;">​</a></h1><p>A. The Department shall establish, staff and maintain, at any state correctional facility designated by the Director, programs and housing for the rehabilitation, training and confinement of persons committed to the Department under the provisions of Article 2 (§ <a href="/vacode/19.2-311/">19.2-311</a> et seq.) of Chapter 18 of Title 19.2. Persons admitted to these facilities shall be determined by the Department to have the potential for rehabilitation through confinement and treatment therein.</p><p>B. Elements of the program shall include but not be limited to (i) an initial period of military style drill, (ii) cognitive behavioral restructuring designed to teach responsibility and accountability through anger management, life skills development, substance abuse education, parenting skills development and peer tutoring, (iii) developmental counseling as needed, (iv) academic education, career and technical education, and apprenticeships, and (v) transitional release, reentry services, aftercare and intensive parole supervision.</p><p>Code 1950, § 53-128.1; 1966, c. 482; 1974, cc. 44, 45; 1982, c. 636; 1990, c. 701; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0690" target="_blank" rel="noreferrer">690</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0483" target="_blank" rel="noreferrer">483</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0759" target="_blank" rel="noreferrer">759</a>.</p>',4),o=[n];function s(l,c,d,p,f,h){return t(),i("div",null,o)}const u=e(r,[["render",s]]);export{g as __pageData,u as default};
