import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 53.1-66 Transfer of prisoners to other facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-66.md","filePath":"vacode/53.1-66.md"}'),a={name:"vacode/53.1-66.md"},o=i('<h1 id="§-53-1-66-transfer-of-prisoners-to-other-facilities" tabindex="-1">§ 53.1-66 Transfer of prisoners to other facilities. <a class="header-anchor" href="#§-53-1-66-transfer-of-prisoners-to-other-facilities" aria-label="Permalink to &quot;§ 53.1-66 Transfer of prisoners to other facilities.&quot;">​</a></h1><p>Any person confined by the Department in a facility established by this chapter may be transferred from such facility to other facilities in the state corrections system for the remainder of the period of commitment under § <a href="http://law.lis.virginia.gov/vacode/16.1-272/" target="_blank" rel="noreferrer">16.1-272</a> or Article 2 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-311/" target="_blank" rel="noreferrer">19.2-311</a> et seq.) of Chapter 18 of Title 19.2, upon a written finding by the Department submitted to the sentencing court that the person has exhibited intractable behavior or, in the case of persons committed under § <a href="http://law.lis.virginia.gov/vacode/19.2-311/" target="_blank" rel="noreferrer">19.2-311</a>, otherwise becomes ineligible to use such facilities pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-311/" target="_blank" rel="noreferrer">19.2-311</a>.</p><p>&quot;Intractable behavior&quot; means behavior which (i) indicates an inmate&#39;s unwillingness or inability to conform his behavior to that necessary to his successful completion of the program or (ii) is so disruptive as to threaten the successful completion of the program by other participants.</p><p>Code 1950, § 53-128.4; 1966, c. 482; 1982, c. 636; 1990, c. 701; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0755" target="_blank" rel="noreferrer">755</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>.</p>',4),s=[o];function n(c,l,h,f,p,d){return t(),r("div",null,s)}const g=e(a,[["render",n]]);export{m as __pageData,g as default};
