import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-67 Admission to facility; good conduct allowance restricted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-67.md","filePath":"vacode/53.1-67.md"}'),o={name:"vacode/53.1-67.md"},i=r('<h1 id="§-53-1-67-admission-to-facility-good-conduct-allowance-restricted" tabindex="-1">§ 53.1-67 Admission to facility; good conduct allowance restricted. <a class="header-anchor" href="#§-53-1-67-admission-to-facility-good-conduct-allowance-restricted" aria-label="Permalink to &quot;§ 53.1-67 Admission to facility; good conduct allowance restricted.&quot;">​</a></h1><p>In no case shall a person previously confined in a youthful offender facility, whether for a different or the same offense, be confined again in such a facility, except for the purposes of study, testing and diagnosis.</p><p>The provisions of §§ <a href="http://law.lis.virginia.gov/vacode/53.1-191/" target="_blank" rel="noreferrer">53.1-191</a>, <a href="http://law.lis.virginia.gov/vacode/53.1-196/" target="_blank" rel="noreferrer">53.1-196</a>, and <a href="http://law.lis.virginia.gov/vacode/53.1-198/" target="_blank" rel="noreferrer">53.1-198</a> through <a href="http://law.lis.virginia.gov/vacode/53.1-201/" target="_blank" rel="noreferrer">53.1-201</a> relating to good conduct credits and allowances and extraordinary service and the provisions of § <a href="http://law.lis.virginia.gov/vacode/53.1-187/" target="_blank" rel="noreferrer">53.1-187</a> relating to credit for time served in a correctional facility or juvenile detention facility shall not apply to persons sentenced to an indeterminate sentence under § <a href="http://law.lis.virginia.gov/vacode/19.2-311/" target="_blank" rel="noreferrer">19.2-311</a> for a crime committed on or after July 1, 1983. Acts performed by such persons which would earn credit for them under § <a href="http://law.lis.virginia.gov/vacode/53.1-191/" target="_blank" rel="noreferrer">53.1-191</a>, if it were applicable, shall be noted on their record by the authorities of the facility.</p><p>Code 1950, § 53-128.5; 1966, c. 482; 1982, c. 636; 1983, c. 606; 1984, c. 313; 1990, c. 701.</p>',4),n=[i];function c(l,s,d,f,h,p){return a(),t("div",null,n)}const v=e(o,[["render",c]]);export{g as __pageData,v as default};
