import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 53.1-82.3 Budgeting schedule for jail projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/53.1-82.3.md","filePath":"vacode/53.1-82.3.md"}'),o={name:"vacode/53.1-82.3.md"},i=a('<h1 id="§-53-1-82-3-budgeting-schedule-for-jail-projects" tabindex="-1">§ 53.1-82.3 Budgeting schedule for jail projects. <a class="header-anchor" href="#§-53-1-82-3-budgeting-schedule-for-jail-projects" aria-label="Permalink to &quot;§ 53.1-82.3 Budgeting schedule for jail projects.&quot;">​</a></h1><p>A. Any city or county or any combination of cities or counties requesting state financial assistance pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/53.1-80/" target="_blank" rel="noreferrer">53.1-80</a>, <a href="http://law.lis.virginia.gov/vacode/53.1-81/" target="_blank" rel="noreferrer">53.1-81</a> or § <a href="http://law.lis.virginia.gov/vacode/53.1-82/" target="_blank" rel="noreferrer">53.1-82</a> shall, on or before March 1 biennially in the odd-numbered years, submit to the Governor, in a format prescribed by the Department of Corrections for such purpose, a community-based corrections plan and specifications, including detailed cost estimates of any facility construction. On or before July 1 in the odd-numbered years, such localities shall also submit to the Governor, in a format prescribed for such purpose by the Department of the Treasury, the expected financing costs for any such facility construction in accordance with § <a href="http://law.lis.virginia.gov/vacode/53.1-82.2/" target="_blank" rel="noreferrer">53.1-82.2</a>. The Governor shall submit his recommendations for funding such projects as part of the budget bill on or before December 20 of the year immediately prior to the beginning of each regular session held in an even-numbered year of the General Assembly. Requests for appropriations of such funds shall be considered by the General Assembly only in even-numbered years.</p><p>B. In the event that the state share of reimbursable costs of the jail facility is estimated to be less than or equal to $1,000,000, such localities shall be exempt from submitting to the Governor, in a format prescribed for such purpose by the Department of the Treasury, the expected financing costs for any such facility construction in accordance with subsection A above, unless such localities seek reimbursement of financial costs associated with such facility construction.</p><p>1993, cc. 387, 787; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0305" target="_blank" rel="noreferrer">305</a>; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0498" target="_blank" rel="noreferrer">498</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0504" target="_blank" rel="noreferrer">504</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1040" target="_blank" rel="noreferrer">1040</a>.</p>',4),n=[i];function s(c,l,h,f,u,d){return r(),t("div",null,n)}const b=e(o,[["render",s]]);export{g as __pageData,b as default};
