import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-1106.2 Additional monetary penalty for certain violations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1106.2.md","filePath":"vacode/54.1-1106.2.md"}'),i={name:"vacode/54.1-1106.2.md"},r=o('<h1 id="§-54-1-1106-2-additional-monetary-penalty-for-certain-violations" tabindex="-1">§ 54.1-1106.2 Additional monetary penalty for certain violations. <a class="header-anchor" href="#§-54-1-1106-2-additional-monetary-penalty-for-certain-violations" aria-label="Permalink to &quot;§ 54.1-1106.2 Additional monetary penalty for certain violations.&quot;">​</a></h1><p>A. If the Board finds any person licensed under the provisions of this chapter to be in violation of a statute or regulation involving fraudulent or improper or dishonest conduct as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-1118/" target="_blank" rel="noreferrer">54.1-1118</a>, which violation occurred while engaged in a transaction initiated arising from a declared state of emergency as defined in § <a href="http://law.lis.virginia.gov/vacode/44-146.16/" target="_blank" rel="noreferrer">44-146.16</a>, the Board shall impose a monetary penalty of up to $10,000 for each such violation.</p><p>B. The penalty imposed pursuant to this section shall be in addition to that provided in § <a href="http://law.lis.virginia.gov/vacode/54.1-202/" target="_blank" rel="noreferrer">54.1-202</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0508" target="_blank" rel="noreferrer">508</a>.</p>',4),n=[r];function l(d,s,c,p,h,f){return a(),t("div",null,n)}const g=e(i,[["render",l]]);export{v as __pageData,g as default};
