import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 54.1-1109 Expiration and renewal of license or certificate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1109.md","filePath":"vacode/54.1-1109.md"}'),i={name:"vacode/54.1-1109.md"},o=t('<h1 id="§-54-1-1109-expiration-and-renewal-of-license-or-certificate" tabindex="-1">§ 54.1-1109 Expiration and renewal of license or certificate. <a class="header-anchor" href="#§-54-1-1109-expiration-and-renewal-of-license-or-certificate" aria-label="Permalink to &quot;§ 54.1-1109 Expiration and renewal of license or certificate.&quot;">​</a></h1><p>A. A license or certificate issued pursuant to this chapter shall expire as provided in Board regulations. Application for renewal of a license or certificate may be made as provided by Board regulations. The application shall be accompanied by a fee set by the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-201/" target="_blank" rel="noreferrer">54.1-201</a>.</p><p>B. With respect to a contractor electing continuous bonding under § <a href="http://law.lis.virginia.gov/vacode/54.1-1106/" target="_blank" rel="noreferrer">54.1-1106</a> or <a href="http://law.lis.virginia.gov/vacode/54.1-1108/" target="_blank" rel="noreferrer">54.1-1108</a>, proof of current bond is required in order to renew the license or certificate. The bond shall commence no later than the effective date of the license and shall expire no sooner than the date of expiration of the license or certificate.</p><p>Code 1950, § 54-131; 1970, c. 319; 1977, c. 640; 1980, c. 634; 1988, c. 765; 1990, c. 911; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0771" target="_blank" rel="noreferrer">771</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1014" target="_blank" rel="noreferrer">1014</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>.</p>',4),n=[o];function c(l,s,f,d,p,h){return r(),a("div",null,n)}const v=e(i,[["render",c]]);export{g as __pageData,v as default};
