import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 54.1-1110 Grounds for denial or revocation of license or certificate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-1110.md","filePath":"vacode/54.1-1110.md"}'),a={name:"vacode/54.1-1110.md"},i=t('<h1 id="§-54-1-1110-grounds-for-denial-or-revocation-of-license-or-certificate" tabindex="-1">§ 54.1-1110 Grounds for denial or revocation of license or certificate. <a class="header-anchor" href="#§-54-1-1110-grounds-for-denial-or-revocation-of-license-or-certificate" aria-label="Permalink to &quot;§ 54.1-1110 Grounds for denial or revocation of license or certificate.&quot;">​</a></h1><p>The Board shall have the power to require remedial education, suspend, revoke, or deny renewal of the license or certificate of any contractor who is found to be in violation of the statutes or regulations governing the practice of licensed or certified contractors in the Commonwealth.</p><p>The Board may suspend, revoke, or deny renewal of an existing license or certificate, or refuse to issue a license or certificate, to any contractor who is shown to have a substantial identity of interest with a contractor whose license or certificate has been revoked or not renewed by the Board. A substantial identity of interest includes but is not limited to (i) a controlling financial interest by the individual or corporate principals of the contractor whose license or certificate has been revoked or nonrenewed, (ii) substantially identical principals or officers, or (iii) the same designated employee as the contractor whose license or certificate has been revoked or not renewed by the Board.</p><p>Additionally, the Board may suspend, revoke or deny renewal of an existing license or certificate, or refuse to issue a license or certificate to any contractor who violates the provisions of Chapter 5 (§ <a href="http://law.lis.virginia.gov/vacode/60.2-500/" target="_blank" rel="noreferrer">60.2-500</a> et seq.) of Title 60.2 and Chapter 8 (§ <a href="http://law.lis.virginia.gov/vacode/65.2-800/" target="_blank" rel="noreferrer">65.2-800</a> et seq.) of Title 65.2.</p><p>Any person whose license is suspended or revoked by the Board shall not be eligible for a license or certificate under any circumstances or under any name, except as provided by regulations of the Board pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-1102/" target="_blank" rel="noreferrer">54.1-1102</a>.</p><p>1980, c. 634, § 54-132.1; 1988, c. 765; 1990, c. 911; 1992, c. 243; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0771" target="_blank" rel="noreferrer">771</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0380" target="_blank" rel="noreferrer">380</a>.</p>',6),n=[i];function c(s,l,d,f,h,p){return r(),o("div",null,n)}const _=e(a,[["render",c]]);export{v as __pageData,_ as default};
